// 1. Get data from the Webhook node
const inputData = $('Webhook - Receive Jobs').first().json;

// 2. Access the 'body' where the jobs are sent
const payload = inputData.body;

// 3. Extract the jobs array (defaults to empty array if missing)
const jobs = payload.jobs || [];

// 4. Return each job as a separate item with search metadata
return jobs.map(job => ({
  json: {
    ...job,
    search_metadata: {
      search_term: payload.search_term,
      location: payload.location,
      timestamp: payload.timestamp
    }
  }
}));

// 1. Get all accumulated items from the Aggregate node
const items = $input.all();
if (items.length === 0) return { json: { total_matches: 0 } };

// 2. Identify all unique columns (headers)
const allKeys = new Set();
items.forEach(item => {
  Object.keys(item.json).forEach(key => allKeys.add(key));
});
const headers = Array.from(allKeys);

// 3. Create the CSV String
let csvString = headers.join(",") + "\n";
items.forEach(item => {
  const row = headers.map(header => {
    let value = item.json[header];
    if (value && typeof value === 'object') value = JSON.stringify(value);
    const cleanValue = (value === null || value === undefined) 
      ? "" 
      : String(value).replace(/"/g, '""').replace(/\n/g, " ");
    return `"${cleanValue}"`;
  });
  csvString += row.join(",") + "\n";
});

// 4. Return the data as a Binary File
return {
  json: {
    total_matches: items.length,
    fileName: `Job_Matches_${new Date().toISOString().split('T')[0]}.csv`
  },
  binary: {
    data: {
      data: Buffer.from(csvString).toString('base64'),
      mimeType: 'text/csv',
      fileName: `Job_Matches_${new Date().toISOString().split('T')[0]}.csv`
    }
  }
};
